using System.Collections;using System.Collections.Generic;using UnityEngine;using Facebook.Unity;public class FB_Initialize : MonoBehaviour{    // Awake function from Unityâ€™s MonoBehavior    void Awake()    {        if (!FB.IsInitialized)        {            // Initialize the Facebook SDK            FB.Init(InitCallback);        }        else        {            // Already initialized, signal an app activation App Event            FB.ActivateApp();        }    }    private void InitCallback()    {        if (FB.IsInitialized)        {            // Signal an app activation App Event            FB.ActivateApp();            // Continue with Facebook SDK            // ...        }    }}